import React, { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todos: []
    };

    this.onSubmit = this.onSubmit.bind(this);
  }

  onSubmit = (event) => {
    alert(this._inputElement.value);
    if (this._inputElement.value != "") {
      this.setState((prevState) => {
        alert(prevState.todos.concat(this._inputElement.value));
        return { 
          todos: prevState.todos.concat(this._inputElement.value)
        };
      });
      alert(this.state.todos[0]);
      this._inputElement.value = "";
    }
    event.preventDefault();
  }

  render() {
    return (
      <div className="App">
        <h1>TODO Application</h1>

        <form onSubmit={this.onSubmit}>
          <h1>Hello {this.state.currentInput}</h1>
          <p>Enter your TODO list, and submit:</p>
          <input
            type='text'
            placeholder='Enter TODO'
            ref={(a) => this._inputElement = a}
          />
          <button type="submit">add</button>
        </form>

        <ul>
          {this.state.todos.map((todo) =>
            <li>{todo}</li>
          )}
        </ul>
      </div>
    );
  }
}

export default App;
