import React, { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentInput: '',
      todos: []
    };
  }

  onInputChange = (event) => {
    this.setState({currentInput: event.target.value});
  }

  onSubmit = (event) => {
    event.preventDefault();
    this.state.todos.push(this.state.currentInput);
    alert("new TODO list added!");
  }

  render() {
    return (
      <div className="App">
        <h1>TODO Application</h1>

        <form onSubmit={this.onSubmit}>
          <h1>Hello {this.state.currentInput}</h1>
          <p>Enter your TODO list, and submit:</p>
          <input
            type='text'
            onChange={this.onInputChange}
          />
          <input
            type='submit'
          />
        </form>

        <ul>
          {this.state.todos.map((todo) =>
            <li>{todo}</li>
          )}
        </ul>
      </div>
    );
  }
}

export default App;
